echo "/* Suspicious Report Styles */
.suspicious-content {
  max-width: 600px;
  width: 90%;
}

.suspicious-item {
  background: #fff;
  border: 1px solid #e74c3c;
  border-radius: 8px;
  padding: 15px;
  margin-bottom: 15px;
  box-shadow: 0 2px 5px rgba(231, 76, 60, 0.2);
}

body.dark-mode .suspicious-item {
  background: #2c3e50;
  border-color: #c0392b;
}

.suspicious-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
  border-bottom: 1px solid #eee;
  padding-bottom: 5px;
}

.suspicious-badge {
  background: #e74c3c;
  color: white;
  padding: 2px 8px;
  border-radius: 4px;
  font-size: 0.8em;
  font-weight: bold;
}

.suspicious-step {
  display: flex;
  align-items: flex-start;
  margin-bottom: 8px;
  font-size: 0.9em;
}

.step-icon {
  margin-left: 10px; /* RTL */
  font-size: 1.2em;
  width: 24px;
  text-align: center;
}

.step-details {
  flex: 1;
}

.step-title {
  font-weight: bold;
  display: block;
  margin-bottom: 2px;
}

.step-info {
  color: #666;
  font-size: 0.9em;
}

body.dark-mode .step-info {
  color: #aaa;
}

.suspicious-profit-loss {
  font-weight: bold;
  direction: ltr;
  display: inline-block;
}

.loss-text { color: #e74c3c; }
.win-text { color: #27ae60; }
.withdraw-text { color: #f39c12; }
" >> d:\inzo\new\style.css
